 namespace App\Http\Controllers;
 use Illuminate\Routing\FormRequest;
 class RegisterFormRequest extends FormRequest
 {
    public function configure()
    {
        $this->request->setAttribute('method', 'POST');
        $this->setAttribute('action', 'register');
        $this->addEventSubscriber(new RegisterFormSubmitted($this->getEventDispatcher()));
    }
}

interface RegisterFormSubmitted
{
    public function submit(FormRequest $formRequest);
}

class RegisterFormSubmitted implements RegisterFormSubmitted
{
    private $dispatcher;
    public function __construct(EventDispatcher $dispatcher)
    {
        $this->dispatcher = $dispatcher;
    }
    public function submit(FormRequest $formRequest)
    {
        $formRequest->getForm()->handleRequest($formRequest)->submit($formRequest);
        $this->dispatcher->dispatch(Form::SUBMIT, $formRequest);
    }
}
