  xhr.send(`{ "text": "${text}" }`);

  // Check the response to ensure the message was sent
  xhr.onload = function() {
    const response = JSON.parse(xhr.responseText);
    if (response.error) {
      console.error('Error sending message to Slack:', response.error);
    } else {
      console.log(`Message sent to Slack channel ${channelId}:`, response.ts);
    }
  };

  xhr.onerror = function() {
    console.error('Error sending message to Slack:', xhr.status, xhr.statusText);
  };
}

// Example usage
postToSlack('Hello, this is a test message!', 'C01234567');
```
