import groovy.util.LinkedHashSet

class ListProcessor {
    void processList(List<String> inputList) {
        // Check if input list is empty or only contains one element
        if (inputList.size() <= 1) {
            println "Input list is empty or only contains one element."
            return []
        }

        def filteredList = []
        def seen = new LinkedHashSet<>()

        for (String element in inputList) {
            if (!seen.contains(element)) {
                seen.add(element)
                filteredList.add(element)
            }
